// This guess the number game is a short TypeScript/Node.js project that allows the user to guess the number generated by the computer. There are also several ways to alter the game, like adding more rounds or displaying the score. Itâ€™s quite simple and uses the random function to generate a number.
var prompt = require("prompt-sync");
var userGuss;
var score = 0;
var rounds = 5;
var attempts = 0;
var min = 1;
var max = 100;
var history = [];
function getRandomNumber() {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function startGame() {
    var ask = prompt({ sigint: true });
    console.log("Hello Welcome to the Guess the Number Game!");
    console.log("I am thinking of a number between ".concat(min, " and ").concat(max, "."));
    console.log("Can you guess it whihch number I think?");
    userGuss = getRandomNumber();
    askQuestion(ask);
}
function askQuestion(ask) {
    if (rounds === 0) {
        console.log("  ahh Sorry Game Over! Your score is: ".concat(score));
        console.log('Your successful attempts: ', history);
        var answer_1 = ask('Do you want to play again game? (y/n) ');
        if (answer_1.toLowerCase() === 'y') {
            score = 0;
            rounds = 10;
            attempts = 0;
            history = [];
            startGame();
        }
        return;
    }
    var answer = ask('Enter a number: ');
    var number = parseInt(answer);
    if (number === userGuss) {
        console.log('Congratulations, you guessed the number!');
        history.push({ userGuss: userGuss, attempts: attempts });
        score++;
        userGuss = getRandomNumber();
        attempts = 0;
    }
    else {
        attempts++;
        if (attempts % 3 === 0) {
            console.log("Hint: The userGuss number is ".concat(number < userGuss ? 'greater' : 'less', " than your guess."));
        }
        else {
            console.log('Incorrect, try again!');
        }
    }
    rounds--;
    console.log("You have ".concat(rounds, " rounds left."));
    askQuestion(ask);
}
startGame();
